INSERT INTO `v9_category` VALUES ('18', '1', 'content', '1', '0', '10', '0,10', '0', '18', '医院简介', ';', 'http://www.cqsjzxyjhyy.com/uploadfile/2018/0515/20180515102955914.jpg', '重庆圣洁中西医结合医院是经卫计委部门批准设立的一所集医疗、预防保健、康复为一体的现代化综合性一级医院，是重庆市城镇职工、城乡居民医疗保险定点医疗机构。其主要医疗团队和技术是由原重庆市渝中区鹅岭骨质增生专科医院发展而来。\r\n      医院位于重庆市两江新区天龙支路10号，医疗用房面积4800平方米。开展床位98张，现有职工近70人，其中拥有中高级卫生技术人员20人，造就和培养了一支常见病、慢性病、老年病、疑难病等多层次需求的具有高素质、高水平、高标准的医疗团队。\r\n      医院目前以中医科、骨质增生专科、', 'yiyuangaikuang/', 'introduction', 'http://www.cqsjzxyjhyy.com/index.php?m=content&c=index&a=lists&catid=18', '0', '0', '{\"ishtml\":\"0\",\"template_list\":\"default\",\"page_template\":\"page\",\"meta_title\":\"\",\"meta_keywords\":\"\",\"meta_description\":\"\",\"qita_child\":\"0\",\"category_ruleid\":\"6\",\"show_ruleid\":\"\",\"repeatchargedays\":\"1\"}', '18', '1', '0', 'yiyuanjianjie', '', '', '', 'Hospital Introduction', '', '');
