INSERT INTO `v9_department_data` VALUES ('43', '<div style=\"text-align: center;\"><img src=\"http://www.cqsjzxyjhyy.com/uploadfile/2018/0514/20180514094502680.jpg\" style=\"height: 299px; width: 400px;\" /></div>\r\n<br />\r\n<span style=\"font-size:14px;\"><span style=\"font-family:微软雅黑;\"><strong>特色科室一：</strong>《中医康复理疗中心》系原重庆鹅岭骨质增生专科发展而来，有着三十年的临床经验，采用中药离子定向导入无痛治疗骨质增生，颈、腰椎间盘突、（膨）出、肩周炎、颈、腰、膝关节退行性变等关节性疾病。同时开展针灸、艾灸、电针、</span></span><span style=\"font-family: 微软雅黑; font-size: 14px;\">微波治疗、</span><span style=\"font-size:14px;\"><span style=\"font-family:微软雅黑;\">牵引、康复训练等理疗项目。</span></span><br />\r\n', '0', '', '0', '0', '', '0', '1', '', '十年的临床经验，采用中药离子定向导入无痛治疗骨质增生，颈、腰椎间盘突、（膨）出、肩周炎、颈、腰、膝关节退行性变等关节性疾病。同时开展针灸推拿、牵引、康复训练等理疗项目。');
INSERT INTO `v9_department_data` VALUES ('44', '<div style=\"text-align: center;\"><br />\r\n<img src=\"http://www.cqsjzxyjhyy.com/uploadfile/2018/0514/20180514115153734.jpg\" style=\"height: 337px; width: 450px;\" /></div>\r\n<span style=\"font-size:14px;\"><span style=\"font-family:微软雅黑;\"><strong>特色科室二：</strong>《中医科》聘请重庆名老中医常年座诊，主要开展中医内科、妇科、儿科、中医肿瘤治疗、中医养生调理等内科常见病，多发病。</span></span><br />\r\n', '0', '', '0', '0', '', '0', '1', '', '主要开展中医内科、妇科、儿科、中医肿瘤治疗、中医养生调理等内科常见病，多发病。');
INSERT INTO `v9_department_data` VALUES ('45', '<div style=\"text-align: center;\"><img alt=\"\" src=\"http://www.cqsjzxyjhyy.com/uploadfile/2018/0408/20180408101811486.jpg\" style=\"width: 400px; height: 533px;\" /></div>\r\n<br />\r\n<span style=\"font-family:微软雅黑;\"><span style=\"font-size:14px;\"><strong>特色科室三：</strong>《妇科》专注女性健康的特色诊疗科室，拥有先进的仪器检查设备及专业<br />\r\n的技术人才，针对妇科检查、妇科炎症及妇科常见病、多发病的诊疗。给你一份清新，还你<br />\r\n一份自信</span></span><br />\r\n', '0', '', '0', '0', '', '0', '1', '', '专注女性健康的特色诊疗科室');
INSERT INTO `v9_department_data` VALUES ('48', '<div>\r\n<p><span style=\"font-size:14px;\"><span style=\"font-family:微软雅黑;\">&nbsp; &nbsp; &nbsp; &nbsp;检验科具有主管技师、技师专业技术力量。</span></span></p>\r\n<p><span style=\"font-size:16px;\"><span style=\"font-family:微软雅黑;\"><span style=\"font-size:14px;\">&nbsp; &nbsp; &nbsp; &nbsp;实验室拥有迈瑞BS&mdash;360E全自动生化分析仪；BC&mdash;5000全自动五分类血球仪；荧光免疫检测仪；血凝仪；血流变检测仪；电解质分析仪等先进设备。</span></span></span><br />\r\n&nbsp;</p>\r\n</div>\r\n<div style=\"text-align: center;\"><span style=\"font-family:微软雅黑;\"><span style=\"font-size:16px;\"><img alt=\"\" src=\"http://www.cqsjzxyjhyy.com/uploadfile/2018/0408/20180408052709980.png\" style=\"width: 400px; height: 296px;\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"http://www.cqsjzxyjhyy.com/uploadfile/2018/0408/20180408052724755.png\" style=\"width: 400px; height: 296px;\" /><br />\r\n<br />\r\n<img alt=\"\" src=\"http://www.cqsjzxyjhyy.com/uploadfile/2018/0408/20180408052738410.png\" style=\"width: 400px; height: 296px;\" /></span></span></div>\r\n<span style=\"font-family:微软雅黑;\"><span style=\"font-size:16px;\">&nbsp;<br />\r\n&nbsp;</span><br />\r\n<span style=\"font-size:14px;\">&nbsp; &nbsp; &nbsp; &nbsp;现开展项目有：血、尿、便三大常规，凝血因子、肝功能、肾功能、血脂、血糖、心肌酶、电解质、风湿三项、肝炎标志物、性激素、肿瘤标志物、支原体、衣原体培养及药敏，并与第三方检测机构合作，开展检查项目过百项。为临床和体检患者提供了可靠的诊断依据。</span></span><br />\r\n', '0', '', '0', '0', '', '0', '1', '', '检验科具有主管技师、技师专业技术力量。');
INSERT INTO `v9_department_data` VALUES ('46', '<span style=\"font-size:14px;\"><span style=\"font-family:微软雅黑;\"><strong>特色科室四：</strong>《内科》为综合性内科，下设心血管、呼吸、内分泌内科及神经、消化、肾内科两个专业组，现有中心供氧及吸引、心电监护仪、十二导联心电图机、动态心电图检测仪、动态血压检测仪等设备，承担各系统常见疾病的预防、诊断、治疗机护理工作、并积累了丰富的疑难病例诊断与治疗经验。病房环境优美、舒适、整洁，医护人员技术过硬、态度和蔼、语言亲切，全体医务人员秉承&ldquo;救死扶伤&rdquo;的服务宗旨，热诚为您提供全程、优质、高效、温馨的服务。</span></span><br />\r\n', '0', '', '0', '0', '', '0', '1', '', '下设心血管、呼吸、内分泌内科及神经、消化、肾内科两个专业组');
