INSERT INTO `v9_people_data` VALUES ('2', '<div style=\"text-align: center;\"><img src=\"http://www.cqsjzxyjhyy.com/uploadfile/2018/0514/20180514084331395.jpg\" style=\"height: 303px; width: 238px;\" /></div>\r\n<p style=\"text-align: center;\"><span style=\"font-size:20px;\"><strong><span style=\"font-family:微软雅黑;\">余文 业务院长&nbsp;</span></strong></span></p>\r\n<p><br />\r\n<span style=\"font-size:14px;\">&nbsp; &nbsp; &nbsp;<span style=\"font-family:微软雅黑;\">&nbsp; 重庆圣洁中西医结合医院业务副院长，主治医师，毕业于第三军医大学临床医学系。出生于医学家庭，受家庭环境影响，立志参军学医。曾任77109部队医院院长15年，工作作风严谨细致，工作态度耐心和蔼。从事临床工作近30年，先后在第三军医大学大坪医院、西南医院、陆军第324医院进修学习，在肝胆外科微创、普外科、预防保健、颈肩腰腿痛疾病等方面有丰富的专业理论知识与临床经验。在《人民军医》、《临床误诊误治》等期刊上发表了多篇论文。因在预防 &ldquo;非典&rdquo;、 &ldquo;禽流感&rdquo;工作中政绩突出荣立个人三等功2次，在单位多次获得&ldquo;优秀基层干部&rdquo;、&ldquo;优秀党员&rdquo;、&ldquo;优秀党务工作者&rdquo;等称号。</span></span></p>\r\n', '0', '', '0', '0', '', '0', '1', '', '医疗、护理、院感、干部保健等工作', ' 重庆圣洁中西医结合医院业务副院长，主治医师');
INSERT INTO `v9_people_data` VALUES ('20', '<div style=\"text-align: center;\"><img src=\"http://www.cqsjzxyjhyy.com/uploadfile/2018/0514/20180514084153849.jpg\" style=\"height: 303px; width: 238px;\" /><br />\r\n<strong style=\"font-family: 微软雅黑; font-size: 16px;\">田会旗 院长 副教授</strong><br />\r\n&nbsp;</div>\r\n<p><span style=\"font-size:16px;\"><sup><span style=\"font-family:微软雅黑;\">&nbsp; &nbsp; &nbsp; &nbsp;重庆圣洁中西医结合医院院长，副主任医师，毕业于第三军医大学临床医学系。曾任77100部队重庆鹅岭骨质增生专科医院院长，从事临床工作30多年，在老年内科、预防保健、颈肩腰腿痛疾病等方面有丰富的专业知识与临床经验。担任鹅岭骨质增生专科医院院长期间，开展了中药定向药物导入治疗、骨质疏松药物加电磁治疗，使骨质增生、椎间盘疾病、骨质疏松症的治愈率有了显著提高，使鹅岭骨质增生专科医院在本市及周边省份享有了很高的知名度。在国家统计源期刊上发表了多篇论文。曾获得&ldquo;成都军区优秀保健工作者&rdquo;、&ldquo;成都军区先进老干部工作者&rdquo;等称号，其领导的单位两次获得&ldquo;全军达标管理先进单位&rdquo;。</span></sup></span></p>\r\n', '0', '', '0', '0', '', '0', '1', '', '医疗、护理、院感、干部保健等工作', '重庆圣洁中西医结合医院院长，副主任医师');
